import { Component } from 'react'

class ErrorBoundary extends Component {
  // método de ciclo de vida
  // quando adicionamos componentDidCatch, 
  // ele transforma a classe um Error Boundary
  componentDidCatch(){

  }

  render() {
    return this.props.children
  }
}
